import React from "react";
import { Image } from "react-bootstrap";

export default function Signup() {
  const [email, setEmail] = useState("");
  const [firstName, setFirstName] = useState("");
  const [lastName, setLastName] = useState("");
  const [password, setPassword] = useState("");

  const singUp = async () =>{
    const url = "https://localhost:5000/user/register"
    const response = await fetch(url, {
      method: "POST",
      headers: {
        "Content-Type": "application/json"
      },
      body: JSON.stringify({
        email,
        firstname: firstName,
        lastname: lastName,
        excerpt_id: this.state.exceprtId
      })
    })
  }
  return (
    <div>
      <div
        style={{
          display: "flex",
          justifyContent: "flex-end",
          marginRight: "30px"
        }}
      >
        <p>Member already?</p>
        <a href="#" style={{ color: "#EA4C89" }}>
          Sign In Now
        </a>
      </div>
      <div className="signup-area">
        <section className="left-signup-area">
          <h3
            style={{
              color: "#865C6C",
              fontWeight: "600",
              textAlign: "center",
              marginTop: "100px",
              marginLeft: "20px",
              marginRight: "20px"
            }}
          >
            Discover the world’s top designers & creatives
          </h3>
          <Image
            style={{ marginTop: "50px" }}
            className="welcome-img"
            src="https://s5.upanh.pro/2019/12/02/bcg-signup.jpg"
            fluid
          />
        </section>

        <section className="container col-8">
          <form className="form-signin col-6 container">
            <h3
              style={{
                fontWeight: "600",
                color: "#333333",
                marginTop: "100px",
                marginBottom: "20px"
              }}
            >
              Sign Up To Panoko
            </h3>
            <p style={{ fontWeight: "600" }}>Email</p>
            <input
              style={{ marginBottom: "20px" }}
              type="email"
              id="inputEmail"
              className="form-control"
              placeholder="Email address"
              required=""
              autofocus=""
              value={email}
              onChange={e => {
                setEmail(e.target.valueÏ);
              }}
            />
            <p style={{ fontWeight: "600" }}>Frist Name</p>
            <input
              style={{ marginBottom: "20px" }}
              type="text"
              id="inputEmail"
              className="form-control"
              placeholder="Frist Name"
              required=""
              autofocus=""
              value={firstName}
              onChange={e => {
                setFirstName(e.target.valueÏ);
              }}
            />
            <p style={{ fontWeight: "600" }}>Last Name</p>
            <input
              style={{ marginBottom: "20px" }}
              type="text"
              id="inputEmail"
              className="form-control"
              placeholder="Last Name"
              required=""
              autofocus=""
              value={lastName}
              onChange={e => {
                setLastName(e.target.valueÏ);
              }}
            />
            <p style={{ fontWeight: "600" }}>Password</p>
            <input
              style={{ marginBottom: "20px" }}
              type="password"
              id="inputPassword"
              className="form-control"
              placeholder="Password"
              required=""
              value={password}
              onChange={e => {
                setPassword(e.target.valueÏ);
              }}
            />
            <div className="checkbox mb-3">
              <label>
                <input type="checkbox" value="remember-me" />
                Remember me
              </label>
            </div>
            <button
              className="btn btn-lg btn-primary btn-block signin-btn-v2"
              type="submit"
            >
              Sign Up
            </button>
            {/* <p className="mt-5 mb-3 text-muted">© 2017-2019</p> */}
          </form>
        </section>
      </div>
    </div>
  );
}
