import React, { useState } from "react";
import { Nav } from "react-bootstrap";
import { useHistory } from "react-router-dom";
import { Modal } from "react-bootstrap";

export default function Comment(props) {
  const [commendBody, setCommentBody] = useState(props.data.body);
  const [openModal, setOpenModal] = useState(false);

  const editPost = async id => {
    const res = await fetch(`https://localhost:5000/post/comment/${id}`, {
      method: "PUT",
      headers: {
        "Content-Type": "application/json",
        Authorization: `Token ${props.token}`
      },
      body: JSON.stringify({ body: comm })
    });
    const data = await res.json();
    if (data.success) {
      console.log("delete success post id:", id);
      history.push("/home");
      setPostData(props.post);
    } else {
      console.log(data.status);
    }
  };

  return (
    <div className=" ">
      <hr className="space-line-comment"></hr>
      <div className="header-comment-area">
        <div>
          <h6>
            <strong>{props.data.user.firstname}</strong>
          </h6>
        </div>

        <div>
          <button
            className="edit-btn-comment"
            onClick={() => setOpenModal(true)}
          >
            Edit
          </button>
          <button className="edit-btn-comment">Delete</button>
        </div>
      </div>
      <span style={{ fontSize: "12px" }} className="text-muted">
        {props.data.created_at}
      </span>

      <p>{props.data.body}</p>

      {/* <p>{props.data.email}</p> */}
      <Modal show={openModal}>
        <Modal.Header>
          <Modal.Title className="d-flex flex-column justify-content-center align-items-center ">
            <strong>Edit Comment</strong>
          </Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <form className="d-flex flex-column justify-content-center  ">
            {/* <div> */}
            <p className="title-input-edit-post">Description</p>
            <input
              name="body"
              className="input-edit-post form-control"
              value={commendBody}
              onChange={e => {
                setCommentBody(e.target.value);
              }}
            ></input>
            {/* </div> */}

            <button
              type="submit"
              className="btn btn-lg btn-primary btn-block signin-btn-v2"
              onClick={editComment(props.data.id)}
            >
              {" "}
              Press this button{" "}
            </button>
          </form>
        </Modal.Body>
      </Modal>
    </div>

    // {PostData.author.firstname}
  );
}
