import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import { Button } from "react-bootstrap";
// import Comment from "../components/Comment"

export default function Post() {
  const [PostData, setPostData] = useState(null);
  const [ liked, setLiked ] = useState(PostData.isLiked)

  const { id } = useParams();

  const handleOnLike = (event) => {
    event.preventDefault()
    fetch(`https://localhost:5000/post/${props.data.id}/like`, {
      method: "GET",
      headers: {
        Authorization: `Token ${localStorage.getItem('token')}`    
      }
    }).then((response) => {
      if(response.json().status === "OK"){
        setLiked(true)
      }else{
        setLiked(false)
      }
    })
  }


  const handleOnLoading = async () => {
    const response = await fetch(`https://localhost:5000/post/${id}`, {
      headers: {
        "Content-Type": "application/json"
      }
    });

    const data = await response.json();
    console.log(PostData);
    setPostData(data);
  };

  useEffect(() => {
    handleOnLoading();
  }, []);

  if (!PostData) {
    return (
      <div>
        <p>Loading...</p>
      </div>
    );
  }

  return (
    <div className="container col-6">
      <div className="artwork-home-area">
        {/* <div className="card mb-4 shadow-sm"> */}
        {/* <svg
            className="bd-placeholder-img card-img-top"
            width="100%"
            height="225"
            xmlns="http://www.w3.org/2000/svg"
            preserveAspectRatio="xMidYMid slice"
            focusable="false"
            role="img"
            aria-label="Placeholder: Thumbnail"
          > */}
        {/* <title>Placeholder</title> */}
        {/* <h5 className="title-single-post">{PostData.title}</h5> */}

        <div className="title-btn-single-post">
          <div>
            <h5 className="title-single-post">{PostData.title}</h5>
          </div>
          <div>
            <button
              type="button"
              className="btn btn-sm btn-outline-secondary edit-btn-single-post"
            >
              Edit
            </button>
            <button type="button" className="btn btn-sm btn-outline-secondary">
              <i class="fas fa-heart"></i> {liked ? "Like" : "Unlike"}
            </button>
          </div>
        </div>
        <div className="author-date-single-post">
          <h5>By {PostData.author.firstname}</h5>

          <small className="text-muted">{PostData.created_at}</small>
        </div>
        <img
          // className="bd-placeholder-img card-img-top"
          width="700"
          height="500"
          src={`${PostData.image_url}`}
        ></img>

        {/* </svg> */}
        <div className="  card-body">
          {/* <p className="card-text">{PostData.title}</p> */}
          <p className="card-text">{PostData.body}</p>
          <div className="d-flex justify-content-between align-items-center">
            <div className="btn-group">
              {/* <button type="button" className="btn btn-sm btn-outline-secondary">
              <Nav.Link href="/single-post">View</Nav.Link> */}
              {/* View */}
              {/* </button> */}
            </div>
          </div>
        </div>

        <hr></hr>

        <div>
          <h6 style={{ marginTop: "35px", marginBottom: "20px" }}>
            <strong>Add a new comment</strong>
          </h6>
          <textarea
            name="body"
            class="form-control form-post-area"
            id="exampleFormControlTextarea1"
            placeholder="Write a comment..."
            rows="3"
          ></textarea>
          <Button
            variant="primary"
            className="upload-btn signin-btn-v2 post-btn"
          >
            Post Comment
          </Button>
        </div>
        {/* <div className="d-flex flex-wrap">
          {comment ? (
            comment.map(post => {
              return <Comment data={comment} />;
            })
          ) : (
            <p>There is not comment</p>
          )}
        </div> */}
      </div>
    </div>
  );
}
