import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom"

export default function Post() {

  const [ data, setData ] = useState({})
  const { id } = useParams()

  const handleOnLoading = async () => {
    const response = await fetch(`https://localhost:5000/post/${id}`)
    const data = await response.json()
    console.log("Data just ficking get here", data)
    setData({...data})
  }

  useEffect(() => {
   handleOnLoading()
  }, [])

  if(data){
    return (
      <div>
        <p>IM FUCKING LOADING, PLEASE WAIT A FUCKING MINUTES</p>
      </div>
    )
  }

  return (

      <div className="col-md-4 artwork-home-area">
        <div className="card mb-4 shadow-sm">
          {/* <svg
            className="bd-placeholder-img card-img-top"
            width="100%"
            height="225"
            xmlns="http://www.w3.org/2000/svg"
            preserveAspectRatio="xMidYMid slice"
            focusable="false"
            role="img"
            aria-label="Placeholder: Thumbnail"
          > */}
            {/* <title>Placeholder</title> */}
            <img
                className="bd-placeholder-img card-img-top"
                width="400"
                height="300"
                src={`${data.image_url}`}>

            </img>

          {/* </svg> */}
          <div className="  card-body">
          <p className="card-text">
              {data.title} - {data.author.id}
          </p>
            <p className="card-text">
              {data.body}
            </p>
            <div className="d-flex justify-content-between align-items-center">
              <div className="btn-group">
                {/* <button type="button" className="btn btn-sm btn-outline-secondary">
              <Nav.Link href="/single-post">View</Nav.Link> */}
                  {/* View */}
                {/* </button> */}
                <button type="button" className="btn btn-sm btn-outline-secondary">
                  Edit
                </button>
              </div>
              <small className="text-muted">{data.created_at}</small>
            </div>
          </div>
        </div>
      </div>




  );
}
