import React, { useState } from 'react'
import { Modal, Button } from "react-bootstrap"
import axios from "axios"
export default function PostEdit(props) {

    const [ modalData, setModalData ] = useState(props.PostData)

    const handleOnChange = (event) => {
        event.preventDefault()
        setModalData({...modalData, [event.target.name]: event.target.value})
    }

    const handleOnSubmit = (event) => {
        event.preventDefault()
        axios({
            url: `https://localhost:5000/post/${props.index}/edit`,
            method: "POST",
            headers: {
                "Content-Type": "application/json",
                Authorization: `Token ${localStorage.getItem("token")}`
            },
            data: JSON.stringify(modalData)
        })
    }

    return (
        <>
            <Modal show={true}>
                <Modal.Header closeButton>
                <Modal.Title>Modal heading</Modal.Title>
                </Modal.Header>
                <Modal.Body>
                    <form
                    onClick={handleOnSubmit}
                    onChange={handleOnChange} 
                    className="d-flex flex-column justify-content-center align-items-center">
                        <input name="image_url" value={modalData.image_url}></input>
                        <img src={modalData ? modalData.image_url : ""} width="300px" alt=""/>
                        <input name="title" value={modalData.title}></input>
                        <input name="body" value={modalData.body}></input>
                        <button type="submit"> Press this button </button>
                    </form>
                </Modal.Body>
            </Modal>
        </>
    )
}
