import React, { useState } from "react";
import { Image } from "react-bootstrap";
import { useHistory, Link } from "react-router-dom";
import notify from "../utils/Notification";
import Form from "react-validation/build/form";
import Input from "react-validation/build/input";
import Button from "react-validation/build/button";
import validator from "validator";
import { required, em } from "../utils/Validation";

export default function Signin(props) {
  const [email, setEmail] = useState();
  const [password, setPassword] = useState("");
  const [disabled, setDisable] = useState(true);
  const history = useHistory();

  // const required = value => {
  //   if (!value.toString().trim().length) {
  //     // We can return string or jsx as the 'error' prop for the validated Component
  //     return <span className="text-danger"> require</span>;
  //   }
  // };

  // const emailValidate = value => {
  //   if (!validator.isEmail(value)) {
  //     return (
  //       <span className="text-danger">{`${value} is not a valid email.`}</span>
  //     );
  //   }
  // };

  const signIn = async e => {
    e.preventDefault();
    const url = `${process.env.REACT_APP_API_URL}/user/login`;
    const response = await fetch(url, {
      method: "POST",
      headers: {
        "Content-Type": "application/json"
      },
      body: JSON.stringify({
        email: email,
        password: password
      })
    });
    if (response.ok) {
      const data = await response.json();
      if (data.code === 200) {
        localStorage.setItem("token", data.apiKey);
        props.setUser(data.user);
        history.push("/home");
        notify("Info", `Welcome ${data.user.email}!`, "success");
      } else if (data.code === 401) {
        props.setUser(null);
        localStorage.removeItem("token");
        notify("Error", "Invalid email or password", "danger");
      }
    }
  };

  return (
    <div>
      {/* <div
        style={{
          display: "flex",
          justifyContent: "flex-end",
          marginRight: "30px"
        }}
      >
        <p>Not a member?</p>

          <a href="#" style={{ color: "#EA4C89" }}>
            Sign Up Now
          </a>
          <Button className="signup-btn mr-2" variant="outline-info">
            <Link className="signup-btn" to="/signup">
              Sign Up
            </Link>
          </Button>

      </div> */}
      <div className="signup-area">
        <div className="left-signin-area">
          <h3
            style={{
              color: "#866117",
              fontWeight: "600",
              textAlign: "center",
              marginTop: "100px",
              marginLeft: "20px",
              marginRight: "20px"
            }}
          >
            Discover the world’s top designers & creatives
          </h3>
          <Image
            style={{ marginTop: "50px" }}
            className="welcome-img"
            src="https://s8.upanh.pro/2019/12/02/bcg-signin.jpg"
            fluid
          />
        </div>

        <div className="container right-signup-area">
          <div
            style={{
              display: "flex",
              justifyContent: "flex-end",
              marginRight: "30px",
              marginTop: "50px"
            }}
          >
            <p>Not a member?</p>

            <a href="#" className="signup-link">
              <Link
                to="/signup"
                style={{ color: "#EA4C89", marginLeft: "10px" }}
              >
                Sign Up
              </Link>
            </a>
          </div>
          <Form className="form-signin" onSubmit={signIn}>
            <h3
              style={{
                fontWeight: "600",
                color: "#333333",
                marginTop: "30px",
                marginBottom: "20px"
              }}
            >
              Sign In To Panoko
            </h3>
            <p style={{ fontWeight: "600" }}>Email</p>

            <Input
              value="email@email.com"
              className="form-control"
              placeholder="Email"
              name="email"
              validations={[required, emailValidate]}
              value={email}
              onChange={e => {
                setEmail(e.target.value);
              }}
            />
            <p style={{ fontWeight: "600" }}>Password</p>
            <Input
              style={{ marginBottom: "20px" }}
              type="password"
              id="inputPassword"
              className="form-control"
              placeholder="Password"
              validations={[required]}
              value={password}
              onChange={e => {
                setPassword(e.target.value);
              }}
            />
            <div className="checkbox mb-3">
              <label>
                <input type="checkbox" value="remember-me" />
                Remember me
              </label>
              <Link
                to="/forgot-password"
                style={{ color: "#EA4C89", marginLeft: "170px" }}
              >
                ForgotPassword
              </Link>
            </div>
            <Button
              className="btn btn-lg btn-primary btn-block signin-btn-v2"
              type="submit"
              // onClick={signIn}
            >
              Sign In
            </Button>
            <hr style={{ marginTop: "20px" }}></hr>
            <a
              className="btn btn-lg btn-primary btn-block signin-fb-btn"
              href={`${process.env.REACT_APP_API_URL}/login/facebook`}
            >
              Sign In With Facebook
            </a>
            <p className="mt-5 mb-3 text-muted">© 2017-2019</p>
          </Form>
        </div>
      </div>
    </div>
  );
}
